RSpec.describe "/product_brands", type: :request do
  include_context :login_user
  # This should return the minimal set of attributes required to create a valid
  # ProductBrand. As you add validations to ProductBrand, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) {
    { name: "Nike" }
  }

  let(:invalid_attributes) {
    { name: nil }
  }

  describe "GET /index" do
    it "renders a successful response" do
      ProductBrand.create! valid_attributes
      get product_brands_url
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      product_brand = ProductBrand.create! valid_attributes
      get product_brand_url(product_brand)
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get new_product_brand_url
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "renders a successful response" do
      product_brand = ProductBrand.create! valid_attributes
      get edit_product_brand_url(product_brand)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new ProductBrand" do
        expect {
          post product_brands_url, params: { product_brand: valid_attributes }
        }.to change(ProductBrand, :count).by(1)
      end

      it "redirects to the created product_brand" do
        post product_brands_url, params: { product_brand: valid_attributes }
        expect(response).to redirect_to(product_brand_url(ProductBrand.last))
      end
    end

    context "with invalid parameters" do
      it "does not create a new ProductBrand" do
        expect {
          post product_brands_url, params: { product_brand: invalid_attributes }
        }.to change(ProductBrand, :count).by(0)
      end

      it "renders a response with 422 status (i.e. to display the 'new' template)" do
        post product_brands_url, params: { product_brand: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_entity)
      end
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) {
        { name: "Nike" }
      }

      it "updates the requested product_brand" do
        product_brand = ProductBrand.create! valid_attributes
        patch product_brand_url(product_brand), params: { product_brand: new_attributes }
        product_brand.reload
      end

      it "redirects to the product_brand" do
        product_brand = ProductBrand.create! valid_attributes
        patch product_brand_url(product_brand), params: { product_brand: new_attributes }
        product_brand.reload
        expect(response).to redirect_to(product_brand_url(product_brand))
      end
    end

    context "with invalid parameters" do
      it "renders a response with 422 status (i.e. to display the 'edit' template)" do
        product_brand = ProductBrand.create! valid_attributes
        patch product_brand_url(product_brand), params: { product_brand: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_entity)
      end
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested product_brand" do
      product_brand = ProductBrand.create! valid_attributes
      expect {
        delete product_brand_url(product_brand)
      }.to change(ProductBrand, :count).by(-1)
    end

    it "redirects to the product_brands list" do
      product_brand = ProductBrand.create! valid_attributes
      delete product_brand_url(product_brand)
      expect(response).to redirect_to(product_brands_url)
    end
  end
end
